import React from 'react';
import './Messages.styles.css';
import OptionsCol from '../OptionsCol/OptionsCol';
import MyFeedHeader from '../MyFeedHeader/MyFeedHeader';
import moment from 'moment';

const Messages = () => {
  const messageData = [{
    id: 1,
    sender_id: '@john_doe',
    receiver_id: '@jane_doe',
    content: 'Lorem Ipsum...',
    timeStamp: 'Sun May 11 2024 11:46:45 GMT+0530',
    status: 'unread'
  },{
    message_id: 2,
    sender_id: '@john_doe',
    receiver_id: '@jane_doe',
    content: 'Lorem Ipsum...',
    status: 'unread',
    timeStamp: 'Sun May 10 2024 11:46:45 GMT+0530' 
  },{
    message_id: 3,
    sender_id: '@john_doe',
    receiver_id: '@jane_doe',
    content: 'Lorem Ipsum...',
    status: 'unread',
    timeStamp: 'Sun May 01 2024 11:46:45 GMT+0530' 
  }];

  return (
    <div className='messages-container'>
        <MyFeedHeader />
        <OptionsCol />
        <div className='messages-main-container'>
          <div className='messages-list'>
            <div className='search-convo'>
              <input type='text' placeholder='Search conversation'/>
            </div>
            <div className='conversations'>
              {/* <div className='conversation-img'>
                <img src='https://via.placeholder.com/150/3357FF/FFFFFF?text=Profile+Pic+03' alt='Profile Pic' />
              </div>
              <div className='conversation-info'>
                <h3>John Doe</h3>
                <div className='conversation-message'>
                  <p>Lorem Ipsum...</p>
                  <p className='timestamp'>3h Ago</p>
                </div>
              </div> */}
              {messageData.map((message, index) => {
                return (
                  <div className='conversation' key={message.id}>
                    <div className='conversation-img'>
                      <img src='https://via.placeholder.com/150/3357FF/FFFFFF?text=Profile+Pic+03' alt='Profile Pic' />
                    </div>
                    <div className='conversation-info'>
                      <h3>{message.fullName}</h3>
                      <div className='conversation-message'>
                        <p>{message.message}</p>
                        <p className='timestamp'>{moment(message.timeStamp).fromNow()}</p>
                      </div>
                    </div>
                  </div>
                )
              })}
            </div>
          </div>
          <div className='messages-content'>

          </div>
        </div>
    </div>
  )
}

export default Messages
